client_script:
let container = document.querySelector('.page_content');
let fchild = document.querySelector('.web-form-container');
let newbtn = document.querySelector('.new-btn');
let div = document.createElement("div")
div.className="cust"
div.textContent="Create Your ERP System Now!"
container.appendChild(div);
container.insertBefore(div, fchild);

const child = fchild.children[0];
child.style.display ='none';

newbtn.textContent="Go to your site now"
newbtn.href="google.com"

